const openSlideMenu=()=>{document.getElementById("side-menu").style.width="250px"},closeSlideMenu=()=>{document.getElementById("side-menu").style.width="0"};let lastKnownScrollPosition=0,ticking=!1;function doSomething(e){let t=document.getElementById("nav-container-id");e>300?t.classList.add("nav-white"):t.classList.remove("nav-white")}function scrollMy(e){setTimeout(()=>{document.getElementById(e).scrollIntoView({behavior:"smooth",block:"center"})},100)}document.addEventListener("scroll",function(e){lastKnownScrollPosition=window.scrollY,ticking||(window.requestAnimationFrame(function(){doSomething(lastKnownScrollPosition),ticking=!1}),ticking=!0)});